import { Banner } from "../../components/BannerAfterLogin";
import aboutImage2 from "../../assets/images/about-image2.png";
import { ChooseLevel } from "../../components/ChooseLevel";
import { Header } from "../../components/Header";
import { ProductsPreview } from "../../components/ProductsPreview";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchProducts, selectAllProducts } from "../../stores/menu/productsSlice";
import ProductDetailCard from "../../components/ProductDetailCard";
import { Tabs } from "../../components/Tabs";
import { addToCart } from "../../stores/cart/cartSlice";

const FinalBeginer = () => {
    

    return (
        <>
            <Banner />
            <div className="bg-white">
            <div className="p-24 grid grid-cols-2">
                <div className="">
                    <h2 className="text-2xl font-medium">About Us</h2>
                    <p className="text-lg">
                    Write a command to display existing nodes \n
                    Kubectl get nodes

                    Create a simple pod using nginx image
                    Kubectl run nginx --image=nginx

                    Run a command to see detailed info. of pod
                    Kubectl get pods -A -owide

                    Perform a Command to describe pod
                    kubectl describe pod pod_name

                    Write a command to create Namespace
                    kubectl create namespace namespace_name
                    </p>
                    <p className="text-lg">
                    In practice level, we will do practice on Kubernetes and we will see how the kubernetes works. Additionally, we also do hands on practice. And below there are some stages which you can be practice for gaining more Knowledge in Kubernetes
                    </p>
                </div>
                <div className="flex items-center justify-center" style={{ marginLeft: '6rem' }}>
                    <img src={aboutImage2} alt="" className="w-[440px] h-[300px] object-cover" />
                </div>
            </div>
        </div>
            
        </>
    )
      

}

export default FinalBeginer;
