import { useState } from "react";
import { useForm } from "react-hook-form";
import Button from "../../components/elements/Button";
import { app } from "../../firebase-config";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { CognitoUser, AuthenticationDetails } from "amazon-cognito-identity-js";
import UserPool from "./UserPool";

const Login = () => {
    let navigate = useNavigate();
    const { register, handleSubmit } = useForm();
    const [loading, setLoading] = useState(false);
    const [email, setEmail] = useState('');

    const onSubmit = (data) => {
        setLoading(true);
        console.log("SUCCESS!!")
        console.log(data.email)
        const usr01=data.email
        console.log(usr01+"success! new var is working")
        console.log(data.password)
        // console.log('https://sktptw0b55.execute-api.ap-south-1.amazonaws.com?user='+data.email)
              fetch('https://sktptw0b55.execute-api.ap-south-1.amazonaws.com?user='+data.email)
              .then(response => response.json())
              .then(data => console.log(data))
              .catch(error => console.error(error));
        const user = new CognitoUser({
            Username: data.email,
            Pool: UserPool
          });
          const authDetails = new AuthenticationDetails({
            Username: data.email,
            Password: data.password
          });
      
          user.authenticateUser(authDetails, {
            onSuccess: data => {
              console.log("onSuccess:", data);
              console.log(data.email)
              // console.log('https://sktptw0b55.execute-api.ap-south-1.amazonaws.com?user='+data.email)
              // fetch('https://sktptw0b55.execute-api.ap-south-1.amazonaws.com?user='+data.email)
              // .then(response => response.json())
              // .then(data => console.log(data))
              // .catch(error => console.error(error));
              
              //window.dispatchEvent(new Event("storage"))
                setLoading(false);
                toast.success('Successful Login!ðŸŽ‰', {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: 'dark'
                    });
                navigate('/menu');
            },
      
            onFailure: err => {
              console.error("onFailure:", err);
              alert(err);
              window.location.href = '/Invalid';
              //props.onFormSwitch('registerverifyuser')
            },
      
            newPasswordRequired: data => {
              console.log("newPasswordRequired:", data);
            }
          });

        // const authentication = getAuth();
        // let uid = '';
        // signInWithEmailAndPassword(authentication, data.email, data.password)
        //     .then((response) => {
        //         uid = response.user.uid;
        //         sessionStorage.setItem('User Id', uid);
        //         sessionStorage.setItem('Auth token', response._tokenResponse.refreshToken)
                // window.dispatchEvent(new Event("storage"))
                // setLoading(false);
                // toast.success('Successful Login!ðŸŽ‰', {
                //     position: "top-right",
                //     autoClose: 5000,
                //     hideProgressBar: false,
                //     closeOnClick: true,
                //     pauseOnHover: true,
                //     draggable: true,
                //     progress: undefined,
                //     theme: 'dark'
                //     });
                // navigate('/');
        //     })
        //     .catch((error) => {
        //         if (error.code === 'auth/wrong-password') {
        //             toast.error('Wrong Password')
        //         }
        //         if (error.code === 'auth/user-not-found') {
        //             toast.error('Email not found, please registe')
        //         }
        //         setLoading(false);
        //     })
    
    }
    return (
        <div className="h-screen bg-black flex  items-center justify-center">
            <div className="rounded-lg max-w-md w-full flex flex-col items-center justify-center relative">
                <div className="absolute inset-0 transition duration-300 animate-pink blur  gradient bg-gradient-to-tr from-rose-500 to-yellow-500"></div>
                <div className="p-10 rounded-xl z-10 w-full h-full bg-black">
                    <h5 className="text-3xl">Login</h5>
                <form className="w-full space-y-6" onSubmit={handleSubmit(onSubmit)}>
                    <div>
                        <label 
                        htmlFor="email"
                        className="block text-lg font-medium text-gray-200">Email</label>
                        <input 
                        {...register('email')}
                        id="email"
                        type="email"
                        className="block appearance-none w-full px-3 py-2 border border-gray-300 roundedn-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-gray-200 focus:border-gray-200"
                        />
                    </div>
                    <div>
                        <label 
                        htmlFor="password"
                        className="block text-lg font-medium text-gray-200">Password</label>
                        <input 
                        {...register('password')}
                        id="password"
                        type="password"
                        className="block appearance-none w-full px-3 py-2 border border-gray-300 roundedn-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-gray-200 focus:border-gray-200"
                        />
                    </div>
                    <Button size="large">{loading ? "loading" : 'Login'}</Button>
                </form>
                <ToastContainer />
                </div>
            </div>
        </div>
    )
}

export default Login;
